export function useEscrowInputFlow() {
  const startEscrowInputFlow = () => {
    flow = new Flow();

    return flow.start(라우트.결제_에스크로);
  };

  const endEscrowInputFlow = flow.end;

  return {
    startEscrowInputFlow,
    endEscrowInputFlow,
  };
}

